import React, { useEffect, useState } from "react";
import styled from "styled-components";

import { ReactComponent as Kiyo } from "../../assets/logo.svg";

import MusicGenerator from "../generator/musicGenerator.js";

import { withRouter, Link } from "react-router-dom";

const GoBackContainer = styled.div`
  width: 270px;
  display: flex;
  justify-content: left;
`;

const AvatarBannerContainer = styled.img`
  margin-top: 41px;
  width: 350px;
  filter: drop-shadow(1px 2px 0px #000);
  // margin-left: 50px;
`;

const BannerContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const Kiiyo = styled(Kiyo)`
  height: 36px;
  width: 84px;
`;

const Container = styled.div`
  // width: 100%;
  width: 452px;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 17px 0 64px 0;

  @media screen and (max-width: 768px) {
    margin: 20px 0 41px 0;
  }
`;

const LogoContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: left;
  margin-right: 30px;
  margin-bottom: 20px;
`;

const AssetContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const RowContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-top: 15px;
`;

const KiiyoTextContainer = styled.text`
  font-size: 11px;
  margin-top: 6px;
  margin-left: 6px;
`;

const UpperDescription = styled.div`
  color: #00629e;
  font-size: 20px;
  font-weight: 700;
  margin-bottom: 30px;
`;

const NoticeTextContainer = styled.div`
  font-weight: normal;
  font-size: 16px;
  margin-top: 36px;
  color: white;

  @media screen and (max-width: 768px) {
    font-size: 13px;
    margin-top: 34px;
  }
`;

const ParticipantsTextContainer = styled.div`
  font-weight: normal;
  font-size: 22px;
  margin: 91px auto 0;
  width: 90px;
  box-shadow: inset 0 -14px 0 #ffd771;
  line-height: 20px;

  @media screen and (max-width: 768px) {
    width: 75px;
    font-size: 18px;
    margin-top: 33px;
  }
`;

const NumberTextContainer = styled.div`
  font-weight: bold;
  font-size: 30px;
  margin-top: 22px;
  display: flex;
  justify-content: center;

  @media screen and (max-width: 768px) {
    font-size: 24px;
    margin-top: 14px;
  }
`;

const ShareTextContainer = styled.div`
  font-weight: normal;
  font-size: 22px;
  margin-top: 83px;

  @media screen and (max-width: 768px) {
    font-size: 18px;
    margin-top: 71px;
  }
`;

const MainPage = ({ history }) => {
  const [backSize, setBackSize] = useState();
  const currentUrl = window.location.href;

  const goBack = () => {
    history.goBack();
  };

  useEffect(() => {
      setBackSize('100%')
  })

  return (
      <div style={{ display: "flex", justifyContent: "center" }}>
        <Container>
          <>
          {backSize?
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                width: "100%",
                textAlign: "center",
                alignItems: "center",
                backgroundColor: "white",
              }}
            >
              <div
                style={{
                  width: "100%",
                  backgroundSize: backSize, 
                //   background: `url(${AvatarBackground}) 0% 0% / 100% no-repeat`,
                }}
              >
                {/* <BannerContainer>
                  <AvatarBannerContainer alt="Banner" src={AvatarBanner} />
                </BannerContainer> */}
                <RowContainer style={{ marginTop: "32px" }}>
                </RowContainer>
                <UpperDescription>
                  당신의 사진을 이 곳에 넣으면
                  <br />
                  AI가 캐릭터로 바꿔줄거에요!
                </UpperDescription>

                <MusicGenerator />

              </div>
            </div>:<></>}
          </>
        </Container>
      </div>
  );
};

export default MainPage;
